mapper.insert:
/* BoardMapper.insertBoard */
    INSERT INTO board (
        board_type
        , contents
        , title
        , registrant
        , modifier
        , reg_date
        , mod_date
    ) VALUES (
        '1'
        , #{contents}
        , #{title}
        , #{registrant}
        , #{modifier}
        , #{regDate}
        , #{modDate}
    )

mapper.select:
/* BoardMapper.insertBoard */
    SELECT *
    FROM board

    ORDER BY reg_date DESC
    LIMIT #{startIndex}, #{pageSize}

